const HamburgerOpenMenu = () => (
  <div className="hamburger-menu-container">
    <div className="nested-menu-container">
      <p>ABOUT ME</p>
      <p>PORTFOLIO</p>
      <p>SKILLS</p>
      <p>CV</p>
      <div className="get-in-touch">
        <p>GET IN TOUCH</p>
        <div className="social-icons">
          <img className="fb" src="/img/fb.png" alt="fb" />
          <img className="linkedin" src="/img/linkedin.png" alt="linkedin" />
          <img className="mail" src="/img/mail.png" alt="mail" />
        </div>
      </div>
    </div>

    {style}
  </div>
);

const style = (
  <style jsx>{`
    .hamburger-menu-container {
      background-color: var(--color1);
      height: 100vh;
      width: 100vw;
      position: absolute;
      left: 0;
      top: 0;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    .nested-menu-container {
      background-color: white;
      height: 85%;
      width: 100%;
      padding: 2rem;
      margin: 2rem;
      font-size: 1.15rem;
      font-family: var(--font3);
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
    }
    .nested-menu-container > p {
      padding: 1rem;
    }
    .social-icons > img {
      width: 2rem;
      padding: 2rem;
      height: auto;
    }
  `}</style>
);

export default HamburgerOpenMenu;
