import { useEffect, useState, useRef } from "react";
import Layout from "../components/Layout";
import Hello from "../components/Hello";

function Index() {
  // const [hasScrolled, sethasScrolled] = useState(false);
  // const handleScroll = () => {
  //   if (window.scrollY > 150) {
  //     sethasScrolled(true);
  //   } else {
  //     sethasScrolled(false);
  //   }
  // };
  const scrollDownToHelloSection = () => {
    console.log("yo");
    helloSection &&
      window.scrollTo({
        behavior: "smooth",
        top: helloSection.current.offsetTop
      });
  };
  // useEffect(() => {
  //   window.addEventListener("scroll", handleScroll);

  //   return () => window.removeEventListener("scroll", handleScroll);
  // }, []);
  const helloSection = useRef();
  helloSection && console.log(helloSection);
  return (
    <div>
      <Layout>
        <div className="landing-page-container">
          <div className="landing-logo-container">
            <div className="landing-logo-wrapper">
              <img className="avatar" src="/img/avatar1.png" alt="avatar" />
              <h1 className="over">over-</h1>
              <h1 className="dub">duB</h1>
              <hr></hr>
              <h2 className="web">WEB DEV.</h2>
            </div>
          </div>
          <div
            className="home-arrow-container"
            onClick={console.log("test yo")}
          >
            <img className="home-arrow" src="/img/arrow.png" alt="arrow" />
          </div>
        </div>
        <div className="hello-section-pre-scroll">
          <Hello />
        </div>
      </Layout>

      {style}
    </div>
  );
}

const style = (
  <style jsx>{`
    .landing-page-container {
      visibility: visible;
      opacity: 1;
      transition: visibility 3s, opacity 2s linear;
      position: relative;
    }
    .landing-logo-container {
      height: 100vh;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      position: relative;
    }
    .avatar {
      width: 5.5rem;
      height: auto;
      margin-left: -0.5rem;
      position: relative;
      z-index: -1;
    }

    .over {
      color: var(--color2);
      font-family: var(--font2);
      font-size: 3.5rem;

      margin-top: -1rem;
      position: relative;
      z-index: -1;
    }
    .dub {
      color: var(--color2);
      font-family: var(--font2);
      font-size: 6rem;
      margin-top: -1.6rem;
      position: relative;
      z-index: -1;
    }
    h1,
    h2 {
      margin-block-start: 0;
      margin-block-end: 0;
      position: relative;
      z-index: -1;
    }
    hr {
      margin-block-start: 0;
      margin-block-end: 0;
      border-color: white;
      width: 5.5rem;
      border-top: 1px solid white;
      margin-inline-start: 5rem;
      margin-top: -1.3rem;
      margin-inline-end: 0;
      position: relative;
      z-index: -1;
    }
    .web {
      font-family: var(--font3);
      color: white;
      font-size: 1.15rem;
      margin-left: 5.2rem;
      margin-top: 0.1rem;
      position: relative;
      z-index: -1;
    }
    .home-arrow-container {
      display: flex;
      justify-content: center;
      align-items: flex-end;
      margin-bottom: 10rem;
      transform: translateY(-4rem);
      position: relative;
    }
    .home-arrow {
      width: 2rem;
      height: auto;
    }
    .logo-fades {
      visibility: hidden;
      opacity: 0;
      position: relative;
      z-index: -1;
    }
    .hello-section-pre-scroll {
      margin-top: -5rem;
      opacity: 1;
      transition: opacity 1s linear;
      position: relative;
    }
    @media (min-width: 801px) {
      .landing-logo-wrapper {
        transform: scale(1.4) translateZ(-1em);
      }
    }
  `}</style>
);

export default Index;
