import Link from "next/link";

const HamburgerOpenMenu = () => (
  <div className="hamburger-menu-container">
    <div className="nested-menu-container">
      <Link href="/about">ABOUT ME</Link>
      <Link href="/portfolio">PORTFOLIO</Link>
      <Link href="/skills">SKILLS</Link>
      <Link href="/cv">CV</Link>
      <div className="get-in-touch">
        <p>GET IN TOUCH</p>
        <div className="social-icons">
          <a href="https://www.facebook.com/jeremieemk" target="_blank">
            <img className="fb" src="/img/fb.png" alt="fb" />
          </a>

          <img className="linkedin" src="/img/linkedin.png" alt="linkedin" />
          <img className="mail" src="/img/mail.png" alt="mail" />
        </div>
      </div>
    </div>

    {style}
  </div>
);

const style = (
  <style jsx>{`
    .hamburger-menu-container {
      background-color: var(--color1);
      height: 100vh;
      width: 100vw;
      position: absolute;
      left: 0;
      top: 0;
      display: flex;
      align-items: center;
      justify-content: center;
    }
    .nested-menu-container {
      background: -webkit-linear-gradient(165deg, white 65%, #f6f6f6 65%);
      background: -o-linear-gradient(165deg, white 65%, #f6f6f6 65%);
      background: -moz-linear-gradient(165deg, white 65%, #f6f6f6 65%);
      background: linear-gradient(165deg, white 65%, #f6f6f6 65%);
      height: 85%;
      width: 100%;
      padding: 2rem;
      margin: 2rem;
      font-size: 1.05rem;
      font-family: var(--font3);
      letter-spacing: 0.1rem;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
    }
    .nested-menu-container > a {
      padding: 1rem;
    }
    .social-icons > img,
    a {
      width: 1.5rem;
      padding: 1rem;
      height: auto;
    }
    .get-in-touch {
      text-align: center;
      transform: translateY(8rem);
    }
    .get-in-touch > p {
      color: black;

      font-size: 0.8rem;
    }
  `}</style>
);

export default HamburgerOpenMenu;
