function LinkBox(props) {
  return (
    <div>
      <div className="text-box-container">
        <div className="text-box">
          <a className="text">{props.name}</a>
        </div>
        <div className="arrow-box">
          <img className="arrow" src="/img/whitearrow2.png" alt="arrow" />
        </div>
      </div>
      {style}
    </div>
  );
}

const style = (
  <style jsx>{`
    .text-box-container {
      margin-top: 1rem;
      display: flex;
    }
    .text-box {
      color: white;
      font-family: var(--font3);
      border: var(--line-width) solid white;
      font-size: 1.3rem;
      width: 12rem;
      height: 3rem;
      display: flex;
      justify-content: flex-start;
      align-items: center;
    }
    .arrow-box {
      border-width: var(--line-width) var(--line-width) var(--line-width) 0;
      width: 5rem;
      border-style: solid;
      border-color: white;
    }
    .text {
      padding-left: 2rem;
    }
    .arrow {
      width: 2rem;
      height: auto;
    }
  `}</style>
);

export default LinkBox;
