import Link from "next/link";

const menuItems = ["HELLO", "ABOUT ME", "PORTFOLIO", "SKILLS"];

const DesktopNav = () => (
  <div className="desktop-nav-container">
    <div className="title-plus-line-container">
      <Link href="/index">
        {menuItems.map(item => (
          <a className="menu-item-name">{item[0]}</a>
        ))}
      </Link>
      <div className="bottom-line"></div>
    </div>

    <Link href="/about">
      <a>ABOUT ME</a>
    </Link>
    <Link href="/portfolio">
      <a>PORTFOLIO</a>
    </Link>

    <Link href="/skills">
      <a>SKILLS</a>
    </Link>

    <a
      href="https://res.cloudinary.com/dbslwolfi/image/upload/v1578760331/rss/CV2020_gsc1gi.pdf"
      target="_blank"
    >
      CV
    </a>
    {style}
  </div>
);

const style = (
  <style jsx>{`
    .desktop-nav-container {
      z-index: 1;
      width: 100%;
      position: absolute;
      padding-top: 2rem;
      display: flex;
      justify-content: center;
      align-items: center;
      font-family: var(--font4);
      font-size: 1.2rem;
      background-color: var(--color1);
    }
    .desktop-nav-container > * {
      padding: 2rem;
    }

    .bottom-line {
      padding-top: 0.5rem;
      width: 1.2rem;
      border-bottom: 0.2rem solid white;
      margin-left: 1rem;
      display: none;
      position: absolute;
    }
    .menu-item-name:hover + .bottom-line {
      display: block;
    }

    @media (max-width: 801px) {
      .desktop-nav-container {
        display: none;
      }
    }
  `}</style>
);

export default DesktopNav;
