    <div className="nested-menu-container">
      <p>ABOUT ME</p>
      <p>PORTFOLIO</p>
      <p>SKILLS</p>
      <p>CV</p>
      <div className="get-in-touch">
        <p>GET IN TOUCH</p>
        <div className="social-icons">
          <img className="fb" src="/img/fb.png" alt="fb" />
          <img className="linkedin" src="/img/linkedin.png" alt="linkedin" />
          <img className="mail" src="/img/mail.png" alt="mail" />
        </div>
      </div>
    </div>

    {style}
  </div>
);

const style = (
  <style jsx>{`
    .hamburger-menu-container {
      background-color: var(--color1);
      height: 100vh;
      width: 100vw;
