import Header from "./Header";
import GlobalStyles from "../style/GlobalStyles";

const Layout = props => (
  <div>
    <div className="container">
      <Header />
      {props.children}
      <div className="footer">
        Built with react.js and open sourced on GitHub
      </div>
    </div>
    {GlobalStyles}
    {style}
  </div>
);

const style = (
  <style jsx>{`
    .container {
      position: relative;
      width: 100%;
    }
    .footer {
      position: absolute;
      bottom: -2;
      text-align: center;
      color: white;
      font: var(--font4);
    }
  `}</style>
);

export default Layout;
