const HelloText = () => (
  <div className="hello-text">
    <p className="upper-text">
      I’m Jérémie. I am a <span className="yellow">coder</span> , a music
      producer <span className="">and a nerdy polyglot. </span>
      <br />
      <p className="smaller2">
        I was born in France and I am currently based in Lisbon, Portugal.
      </p>
      <div className="img-container">
        <img className="sardine" src="/img/toilet.png" alt="sardine" />
      </div>
    </p>
    {style}
  </div>
);

const style = (
  <style jsx>{`
    .hello-text {
      width: 70%;
      color: white;
      font-family: var(--font2);
      margin-top: 2rem;
      padding-left: 3rem;
      padding-right: 3rem;
      text-align: left;
    }
    .sardine {
      margin-top: 2rem;
      width: 12rem;
      max-height: 100%;
    }
    .img-container {
      display: flex;
      justify-content: center;
    }
    .upper-text {
      font-size: 1.6rem;
    }
    .smaller {
      font-size: 1.1rem;
    }
    .smaller2 {
      font-size: 1.3rem;
      line-height: 1.5rem;
      font-family: var(--font5);
    }
    .yellow {
      color: var(--color2);
      font-size: 2rem;
      font-family: var(--font3);
    }
    @media (min-width: 801px) {
      .upper-text {
        font-size: 2.3rem;
        font-family: var(--font4);
      }
      .smaller2 {
        font-size: 1.8rem;
        line-height: 2.4rem;
        font-family: var(--font4);
      }
      .yellow {
        font-size: 2.3rem;
        font-family: var(--font4);
      }
      .sardine {
        margin-top: 1.5rem;
        width: 15rem;
      }
    }
  `}</style>
);

export default HelloText;
