import Header from "./Header";
import GlobalStyles from "../style/GlobalStyles";

const Layout = props => (
  <div>
    <div className="container">
      <Header />
      {props.children}
      <div className="footer">
        (Built with react.js and open sourced on{" "}
        <a href="https://github.com/jeremieemk/overdub/" target="_blank">
          GitHub
        </a>
        )
      </div>
    </div>
    {GlobalStyles}
    {style}
  </div>
);

const style = (
  <style jsx>{`
    .container {
      position: relative;
      width: 100%;
    }
    .footer {
      width: 100%;
      position: absolute;
      bottom: -2;
      margin-bottom: 1rem;
      margin-top: 2rem;
      text-align: center;
      color: white;
      font-family: var(--font4);
      font-size: 0.8rem;
    }
    .footer > a {
      color: var(--color2);
    }
  `}</style>
);

export default Layout;
