import Header from "./Header";
import Head from "next/head";
import GlobalStyles from "../style/GlobalStyles";
import SocialIcons from "./SocialIcons";

function Layout(props) {
  return (
    <div>
      <Head>
        <link rel="shortcut icon" href="/favicon.ico" />
        <link rel="stylesheet" href="/css/priority.css"></link>
        <script
          async
          src="https://www.googletagmanager.com/gtag/js?id=UA-115969774-2"
        ></script>
        <script
          dangerouslySetInnerHTML={{
            __html: `<!-- Global site tag (gtag.js) - Google Analytics -->

                      <script>
                        window.dataLayer = window.dataLayer || [];
                        function gtag(){dataLayer.push(arguments);}
                        gtag('js', new Date());

                        gtag('config', 'UA-115969774-2');
                      </script>`,
          }}
        />
      </Head>
      <div className="container">
        <Header />
        {props.children}
        <div className="footer">
          <div className="open-source">
            (Built with react.js and open-sourced on{" "}
            <a
              className="github-link"
              href="https://github.com/jeremieemk/overdub/"
              target="_blank"
            >
              GitHub
            </a>
            )
          </div>
          <SocialIcons />
        </div>
      </div>
      {GlobalStyles}
      {style}
    </div>
  );
}

const style = (
  <style jsx>{`
    .container {
      position: relative;
      width: 100%;
    }
    .footer {
      width: 100%;
      position: absolute;
      bottom: -2;
      margin-bottom: 1rem;
      margin-top: 2rem;
      text-align: center;
      color: white;
      font-family: var(--font4);
      font-size: 0.8rem;
    }
    .github-link {
      color: var(--color2);
    }
    .open-source {
      margin-bottom: 0.5rem;
    }
  `}</style>
);

export default Layout;
